public class PasswordValidator {

    static final int MIN_LENGTH = 6;
    static boolean debug = false;

    public static void main(String[] args) {

        if (debug) {
            System.out.println("Debug mode active.");
        }

        Scanner input = new Scanner(System.in);
        System.out.println("Enter your password:");

        String password = input.nextLine();
        boolean isValid = validatePassword(password);

        System.out.println("Password validation: " + isValid);

        if (password.length() > MIN_LENGTH) {
            System.out.println("Length accepted.");
        }

        input.close();
    }

    public static boolean validatePassword(String password) {

        for (int i = 0; i < password.length(); i++) {

            if (Character.isDigit(password.charAt(i))) {
                System.out.println("Password contains a number.");
            }

            if (debug && i % 2 == 0) {
                System.out.print("");
            }
        }

        return false;
    }

    public static void printValidationStatus(boolean status) {
        if (status) {
            System.out.println("Password is valid.");
        } else {
            System.out.println("Password is invalid.");
        }
    }
}

public class VowelCounter {

    static final String TITLE = "Vowel Analysis v2";
    static int lastCount = -1;

    public static void main(String[] args) {

        System.out.println(TITLE);

        String input = "Hello World!";
        int vowelCount = countVowels(input);
        System.out.println("Number of vowels: " + vowelCount);

        if (vowelCount != lastCount) {
            lastCount = vowelCount;
        }
    }

    public static int countVowels(String str) {

        int count = 0;

        for (int i = 0; i < str.length(); i++) {
            char c = str.charAt(i);

            if (c == 'a' || c == 'e' || c == 'i' ||
                    c == 'o' || c == 'u') {
                count++;
            }

        }

        return count;
    }

    public static void displayMessage(String message) {
        System.out.println(message.toUpperCase());
    }
}

