import java.util.Scanner;

public class FavoriteColor {
    public static void main(String[] args) {
        Scanner input = new Scanner(System.in);
        System.out.println("What is your favorite color?");
        String color = input.nextLine();
        checkColor(color);
        validateResults(color);
    }

    /*** =========================== [EDIT BELOW ONLY] ===========================*/

    public static void checkColor(String color { 
        if (color.equalsIgnoreCase("blue") {
            System.out.println("Blue is a popular choice!") 
        else if (color.equalsIgnoreCase("red")) {
            System.out.println("Red is vibrant and bold!");
        } else {
            System.out.println("That's a unique choice!");
        }
    }

    /*** =========================== [EDIT ABOVE ONLY] ===========================*/

    public static void validateResults(String color) {
        if (color.equalsIgnoreCase("blue")) {
            System.out.println("Expected output: Blue is a popular choice!");
        } else if (color.equalsIgnoreCase("red")) {
            System.out.println("Expected output: Red is vibrant and bold!");
        } else {
            System.out.println("Expected output: That's a unique choice!");
        }
    }
}
